mapper.select:

select * from room r


        or r.standardPrice LIKE #{rp} or r.vipPrice LIKE #{rp} or r.memberPrice LIKE #{rp} or r.discountPrice LIKE #{rp}


        1=0


select * from room r



        and r.id=#{room.id}


        and r.type=#{room.type}


        and r.floor=#{room.floor}


        and r.`status`=#{room.status}


        and (r.standardPrice LIKE #{rp} or r.vipPrice LIKE #{rp} or r.memberPrice LIKE #{rp} or r.discountPrice LIKE #{rp})

mapper.update:
update room r
set type=#{type},floor=#{floor},status=#{status},
    standardPrice=#{standardPrice},discountPrice=#{discountPrice},
    memberPrice=#{memberPrice},vipPrice=#{vipPrice},remarks=#{remarks}
where id=#{id}
